//This is where the state of our program will live
var cart = [];

function createProduct (product) {
  if (product.available) {
    var $container = $('&lt;div&gt;');
    $container.attr('id', `product-${product.id}`).addClass('product');

    var $productImg = $('&lt;img&gt;');

    if (product.picture) {
      $productImg.attr('src', product.picture);
    } else {
      $productImg.attr('src', '<http://lorempixel.com/32/32>');
    }

    $container.append($productImg);

    var $title = $('&lt;h4&gt;');
    $title.text(product.title);

    var $price = $('&lt;div&gt;');
    $price.text('$' + product.price).addClass('price'); 

    var $description = $('&lt;p&gt;');
    $description.text(product.description);

    $container.append($title);
    $container.append($price);
    $container.append($description);

    return $container;
  }
}

function renderCart() {
  var $cartContainer = $('&lt;div&gt;').addClass('container');
  var $totalDiv = $('&lt;div&gt;').addClass('total');
  var total = 0;

  for (var i = 0; i &lt; cart.length; i++) {
    $productContainer = createProduct(cart[i]);
    total += parseFloat(cart[i].price);
    $cartContainer.append($productContainer);
  }

  $totalDiv.text(`Total: $${total.toFixed(2)}`);
  $cartContainer.append($totalDiv);
  return $cartContainer;
}

var $productList = $('.productList');

for (var i = 0; i &lt; products.length; i++) {
  var $productContainer = createProduct(products[i]);
  var $buyNowButton = $('&lt;button&gt;');
  $buyNowButton.text('Buy Now!');

  if ($productContainer) {
    $productContainer.append($buyNowButton);
    $productList.append($productContainer);
  }
}

$('button').click(function(event) {
  var button = event.target;
  var id = $(button.parentElement).attr('id').split('-')[1];
  var product;

  for (var i = 0; i &lt; products.length; i++) {
    if (products[i].id === parseInt(id)) {
      cart.push(products[i]);
    }
  }

  $('.cart').empty().append(renderCart());
});```

