// Functional Solution
function calcValue(num) {
  if (num % 15 === 0) {
    return 'FizzBuzz ';
  } else if (num % 3 === 0) {
    return 'Buzz ';
  } else if (num % 5 === 0) {
    return 'Fizz ';
  } else {
    return `${num} `;
  }
}

// Ternary Solution
function ternaryFizzer() {
  let fizzBuzzString = '';
  for (let i = 0; i &lt; 100; i++) {
    fizzBuzzString += i % 15 ? 'FizzBuzz' : i % 5 ? 'Buzz' : i % 3 ? 'Fizz' : i;
  }
}

// Generator Solution
function* genFizzer() {
  var i = 0;
  while (true) {
    ++i;
    var x = 0;
    if (i % 3 === 0 &amp;&amp; i % 5 === 0) yield 'Fizz Buzz';
    else if (i % 3 === 0) yield 'Fizz';
    else if (i % 5 === 0) yield 'Buzz';
    else yield i;
  }
}

