class dLinkedListArr {
  // Create the constructor for the class.
  constructor() {
    this.length = 0,
      this.head = null,
      this.tail = null
  }

  push(value) {
    // work with/create your next element.
    var node = {
      value: value,
      next: null,
      last: null
    };

    // handle edge case, no items currently inside of the array.
    if (this.length === 0) {
      this.head = node;
      this.tail = node;
    } else {
      // attach to the tail node here.
      this.tail.next = node;
      node.last = this.tail;
      this.tail = node;
    }

    // update the length.
    this.length++;
  }

  pop() {
    var current = this.head;

    // edge case: remove if there's only one element.
    if (this.length === 1) {
      this.head = current.next;

      if (!this.head) {
        this.tail = null;
      } else {
        this.head.last = null;
      }
    } else { // remove last element.
      current = this.tail;
      this.tail = current.last;
      this.tail.next = null;
    }

    // decrement the length to coordinate with the pop.
    this.length--;

    // return the final value.
    return current.data;
  }
}

